import React, { useEffect, useState } from 'react';
import { List, Tag, Image, message } from 'antd';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utility/axiosInstance';
import { Container } from '../styles/styles';

const HistoryPage = () => {
  interface DataType {
    session_title: string;
    date: string;
    file_name: string;
    thumbnail: string;
    session_type: string;
    isFallback?: boolean; // Add a flag to track fallback status for each item
  }

  const [loading, setLoading] = useState(false);
  const [data, setData] = useState<DataType[]>([]);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  const handleItemClick = (sessionTitle: string) => {
    const sessionId = sessionTitle.replace('Session ID: ', '');
    navigate(`/summary?session_title=${sessionId}`);
  };

  const fetchUserHistory = async () => {
    setLoading(true);
    try {
      const response = await axiosInstance.get('/user/history');
      const newSessions = await Promise.all(
        response.data.map(async (session: any) => {
          const thumbnail = session.thumbnail
            ? await window.electron.video.getThumbnail(session.thumbnail)
            : 'No Thumbnail';
          return {
            session_title: `Session ID: ${session.sitting_session_id}`,
            date: new Date(session.date).toLocaleString(),
            file_name: session.file_name || 'No Files',
            thumbnail,
            session_type: session.session_type,
            isFallback: false, // Initialize fallback state as false for each session
          };
        }),
      );
      setData(newSessions);
    } catch (errors) {
      console.error('Error fetching user history:', errors);
      setError('Failed to fetch user history');
      message.error('Failed to fetch user history');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUserHistory();
  }, []);

  const handleImageError = (index: number) => {
    // Update the specific item to indicate it needs a fallback
    setData((prevData) =>
      prevData.map((item, idx) =>
        idx === index ? { ...item, isFallback: true } : item,
      ),
    );
  };

  return (
    <Container>
      {error ? (
        <p>{error}</p>
      ) : (
        <List
          loading={loading}
          dataSource={data}
          renderItem={(item: DataType, index) => (
            <List.Item
              onClick={() => handleItemClick(item.session_title)}
              style={{
                cursor: 'pointer',
                padding: 35,
                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
                marginBottom: 8,
                borderRadius: 12,
              }}
            >
              <Image
                src={item.thumbnail}
                alt="Thumbnail"
                width={item.isFallback ? 150 : 200}
                height={item.isFallback ? 150 : 'auto'}
                style={{
                  borderRadius: '8px',
                  ...(item.isFallback && {
                    border: '1px solid #ccc', // Example of additional styles for fallback
                  }),
                }}
                preview={false}
                fallback="data:image/png;base64,"
                onError={() => handleImageError(index)} // Set fallback state for each item
              />
              <List.Item.Meta
                title={`Date: ${item.date}`}
                description={item.session_title}
                style={{ marginLeft: '32px' }}
              />
              <Tag
                color={item.session_type === 'stream' ? '#1E90FF' : '#FF9248'}
              >
                {item.session_type}
              </Tag>
            </List.Item>
          )}
        />
      )}
    </Container>
  );
};

export default HistoryPage;
